step.params:
// Store the multipliers that each unit of measurement needs in order
// to calculate the byte count.
var multipliers = {}
multipliers.B = 1;
multipliers.KB = ( multipliers.B * 1024 );
multipliers.MB = ( multipliers.KB * 1024 );
multipliers.GB = ( multipliers.MB * 1024 );
multipliers.TB = ( multipliers.GB * 1024 );
multipliers.PB = ( multipliers.TB * 1024 );
multipliers.EB = ( multipliers.PB * 1024 );
multipliers.ZB = ( multipliers.EB * 1024 );

// Parse the unit of measurement.
var matches = value.toUpperCase().match( /([KMGTPEZ]I?)?B$/ );

// Strip out any optional "i" from the unit of measurement.
var unit = matches[ 0 ].replace( /i/i, "" );

// Parse the number.
var number = parseFloat( value );

// Convert to bytes.
return( Math.floor(number * multipliers[ unit ] ) );

// Get the discovered data from Zabbix.
disco_data = JSON.parse(value);

// Strip legacy "data" element if it was found.
if (disco_data.data) {
  disco_data = disco_data.data;
}

// Iterate over all discovered objects.
for (var lvl1 in disco_data) {

  // Safekeeping
  if (disco_data.hasOwnProperty(lvl1)) {

    // Iterate over all object attributes.
    for (var lvl2 in disco_data[lvl1]) {

      // Safekeeping
      if (disco_data[lvl1].hasOwnProperty(lvl2)) {

        // Check if we found the required attribute.
        if (lvl2 == '{#JMXOBJ}') {

          var objdata = disco_data[lvl1][lvl2];
          // Cut the string to only contain the name of the Solr core.
          var regex = /(.*),category=CORE,name=coreName/;
          var val2 = '';

          if (objdata.match(regex)) {
            val2 = objdata.replace(regex, "$1");
            // Overwrite the original value with the regex match.
            disco_data[lvl1][lvl2] = val2;
          } else {
            val2 = disco_data[lvl1][lvl2];
          }

        }

      }

    }

  }

}

// Return the (modified) discovered data to Zabbix.
return (JSON.stringify(disco_data));
